import{_ as t,c as e,o as r,ae as i}from"./chunks/framework.BBQ6_s69.js";const o="/WHartTest/img/image.png",n="/WHartTest/img/image-1.png",l="/WHartTest/img/image-2.png",s="/WHartTest/img/image-3.png",h="/WHartTest/img/image-4.png",c="/WHartTest/img/image-6.png",g="/WHartTest/img/image-7.png",m="/WHartTest/img/image-8.png",d="/WHartTest/img/image-9.png",p="/WHartTest/img/image-11.png",u="/WHartTest/img/image-10.png",H=JSON.parse('{"title":"项目介绍","description":"","frontmatter":{},"headers":[],"relativePath":"project-introduction.md","filePath":"project-introduction.md","lastUpdated":1756794643000}'),_={name:"project-introduction.md"};function b(q,a,f,P,T,k){return r(),e("div",null,a[0]||(a[0]=[i('<h1 id="项目介绍" tabindex="-1">项目介绍 <a class="header-anchor" href="#项目介绍" aria-label="Permalink to &quot;项目介绍&quot;">​</a></h1><p><strong>WHartTest</strong> 是由 <strong>山东麦港数据系统有限公司</strong> 旗下<strong>麦港实验室（MGdaas Lab）</strong> 推出的开源项目，隶属于 <strong>WHart</strong> 系列。该系列聚焦于为开源社区贡献优质产品与组件，旨在通过技术共享赋能行业生态，推动相关领域的技术创新与应用发展。</p><p><strong>WHartTest</strong> 是基于 <strong>Django REST Framework</strong> 与现代大模型技术打造的 <strong>AI 驱动测试自动化平台</strong>。平台聚合自然语言理解、知识库检索与嵌入搜索能力，结合 <strong>LangChain</strong> 与 <strong>MCP（Model Context Protocol）</strong> 工具调用，实现从需求到可执行测试用例的自动化生成与管理，帮助测试团队提升效率与覆盖率。</p><h2 id="核心价值" tabindex="-1">核心价值 <a class="header-anchor" href="#核心价值" aria-label="Permalink to &quot;核心价值&quot;">​</a></h2><ul><li>智能用例生成：从需求或对话中自动生成结构化测试用例（测试步骤、前置条件、输入、期望结果、优先级等）。</li><li>知识感知：支持从文档、API 文档与知识库抽取上下文，使用嵌入增强模型的理解与检索精度。</li><li>自动评审与建议：基于模型的需求评审与风险提示，辅助测试策略制定与优先级判断。</li><li>用例管理与执行：集中管理用例、执行记录、自动截屏与执行结果上报，便于审计与回溯。</li><li>可扩展与可定制：支持接入自定义模型、第三方服务与扩展工具链（LangChain、HuggingFace 等）。</li></ul><h2 id="技术栈" tabindex="-1">技术栈 <a class="header-anchor" href="#技术栈" aria-label="Permalink to &quot;技术栈&quot;">​</a></h2><ul><li>后端：Django, Django REST Framework</li><li>AI：大语言模型（LLM）、LangChain、HuggingFace 嵌入</li><li>存储：示例使用 SQLite，可切换为 PostgreSQL / MySQL</li><li>前端：Vue + Vite（详见 <code>WHartTest_Vue</code>）</li></ul><h2 id="快速上手-简要" tabindex="-1">快速上手（简要） <a class="header-anchor" href="#快速上手-简要" aria-label="Permalink to &quot;快速上手（简要）&quot;">​</a></h2><ol><li>克隆仓库并进入项目根目录。</li><li>进入 <code>WHartTest_Django</code>，创建虚拟环境并安装依赖：参见 <code>requirements.txt</code>。</li><li>执行数据库迁移并启动服务： <ul><li>python manage.py migrate</li><li>python manage.py runserver</li></ul></li><li>浏览器打开 <a href="http://127.0.0.1:8000" target="_blank" rel="noreferrer">http://127.0.0.1:8000</a> 查看界面（具体配置与部署请参阅根目录 README）。</li></ol><h2 id="界面与功能预览" tabindex="-1">界面与功能预览 <a class="header-anchor" href="#界面与功能预览" aria-label="Permalink to &quot;界面与功能预览&quot;">​</a></h2><p>以下截图展示了平台的典型界面与功能，供快速浏览：</p><h3 id="登录页面" tabindex="-1">登录页面 <a class="header-anchor" href="#登录页面" aria-label="Permalink to &quot;登录页面&quot;">​</a></h3><p><img src="'+o+'" alt="登录页面"></p><h3 id="知识库提取与管理" tabindex="-1">知识库提取与管理 <a class="header-anchor" href="#知识库提取与管理" aria-label="Permalink to &quot;知识库提取与管理&quot;">​</a></h3><p><img src="'+n+'" alt="知识库管理"></p><h3 id="ai-对话与测试用例生成" tabindex="-1">AI 对话与测试用例生成 <a class="header-anchor" href="#ai-对话与测试用例生成" aria-label="Permalink to &quot;AI 对话与测试用例生成&quot;">​</a></h3><p><img src="'+l+'" alt="AI 对话与测试用例生成"></p><h3 id="测试用例管理" tabindex="-1">测试用例管理 <a class="header-anchor" href="#测试用例管理" aria-label="Permalink to &quot;测试用例管理&quot;">​</a></h3><p><img src="'+s+'" alt="测试用例管理"></p><h3 id="生成用例详情" tabindex="-1">生成用例详情 <a class="header-anchor" href="#生成用例详情" aria-label="Permalink to &quot;生成用例详情&quot;">​</a></h3><p><img src="'+h+'" alt="生成用例详情"></p><h3 id="测试执行与自动截屏" tabindex="-1">测试执行与自动截屏 <a class="header-anchor" href="#测试执行与自动截屏" aria-label="Permalink to &quot;测试执行与自动截屏&quot;">​</a></h3><p><img src="'+c+'" alt="测试执行与自动截屏"></p><h3 id="执行结果与报告" tabindex="-1">执行结果与报告 <a class="header-anchor" href="#执行结果与报告" aria-label="Permalink to &quot;执行结果与报告&quot;">​</a></h3><p><img src="'+g+'" alt="执行结果"></p><h3 id="报告详情" tabindex="-1">报告详情 <a class="header-anchor" href="#报告详情" aria-label="Permalink to &quot;报告详情&quot;">​</a></h3><p><img src="'+m+'" alt="报告详情"></p><h3 id="ai-驱动的需求评审与报告" tabindex="-1">AI 驱动的需求评审与报告 <a class="header-anchor" href="#ai-驱动的需求评审与报告" aria-label="Permalink to &quot;AI 驱动的需求评审与报告&quot;">​</a></h3><p><img src="'+d+'" alt="AI 需求评审"></p><h3 id="报告打分与建议" tabindex="-1">报告打分与建议 <a class="header-anchor" href="#报告打分与建议" aria-label="Permalink to &quot;报告打分与建议&quot;">​</a></h3><p><img src="'+p+'" alt="报告打分与建议"></p><h3 id="报告详情-1" tabindex="-1">报告详情 <a class="header-anchor" href="#报告详情-1" aria-label="Permalink to &quot;报告详情&quot;">​</a></h3><p><img src="'+u+'" alt="报告详情"></p><p>（更多细节、API 与部署说明请查阅仓库中的 docs 目录及各模块 README）</p>',34)]))}const W=t(_,[["render",b]]);export{H as __pageData,W as default};
